mapper.select:
SELECT email
  FROM user
 WHERE name     = #{name}
   AND phone_number  = #{phoneNumber}

SELECT *
  FROM user
 WHERE email        = #{email}
   AND name         = #{name}
   AND phone_number = #{phoneNumber}

mapper.update:
UPDATE user
   SET password = #{password}
 where idx      = #{idx}

UPDATE user
SET password = #{password}
where email  = #{email}

UPDATE user
   SET name = #{name}
 WHERE idx  = #{idx}

mapper.select:
SELECT gallery.gallery_id, gallery.idx, gallery.gallery_image_location
FROM gallery
WHERE idx = #{idx}
